import { useState } from 'react'; 

function getRandomAnimal() {
    const animals = ['bird', 'cat', 'cow', 'dog', 'gator', 'horse'];

return animals
}


function App() {

    const [count, setCount] = useState(0);

    const handleClick = () => {
        setCount(count + 1);
    };

    return <div>
        <button onClick={handleClick}>
            Add Animal
        </button>
        <div>
            Number of animals: {count}
        </div>
    </div>;
}

export default App;
